mapper.select:
select c.name as
 subject,c.idsubjects from students a left join sub_stu b on a.idstudents=b.stu_id
 left join subjects c on b.sub_id= c.idsubjects where a.idstudents=#{id}

mapper.update:
update students
studentname=#{studentname}, studentpassword=#{studentpassword}, chinese=#{chinese}, math=#{math}, english=#{english}
where idstudents=#{idstudents}
